import  gql  from "graphql-tag";

export const GET_POKEMONS = gql `
    query pokemons($first: Int!){
        pokemons(first: $first) {
            id
            number
            name
            attacks {
                special {
                    name
                    type
                    damage
                }
            }
            evolutions {
                id
                number
                name
                weight {
                    minimum
                    maximum
                }
                attacks {
                    fast {
                        name
                        type
                        damage
                    }
                }
            }
        }        
    }`
